const OperationButton = (props) => {

    const {setnum, num, newarr, setnewarr} = props


    const OperationClick = (e) => {
        const buttonId = e.target.id
        if(newarr.length === 0){
            setnewarr(newarr => [...newarr, num])
            setnewarr(newarr => [...newarr, (buttonId)])
            setnum('')
        }else {
            switch (newarr[1]) {
                case '+':
                    if (newarr === "") {
                        setnum(0)
                    }
                    setnewarr(newarr=>[...newarr[0] = Number(parseFloat(newarr[0] + num).toPrecision(12)) ])
                    // setnewarr(newarr=>[newarr[1] = buttonId])
                    // setnewarr(newarr[0] = Number(parseFloat(newarr[0] + num).toPrecision(12)))
                    // setnewarr(newarr[1] = buttonId)
                    // newarr[0] = Number(parseFloat(newarr[0] + num).toPrecision(12))
                    // newarr[1] = buttonId
                    console.log(newarr);
                    break;

                default:
                    break;
            }
        }
    }
    console.log(newarr);

    return(

        <div className="operation-button-body">
            <div className="operation-button" id="+" onClick={OperationClick}>+</div>
            <div className="operation-button" id="-" onClick={OperationClick}>-</div>
            <div className="operation-button" id="*" onClick={OperationClick}>*</div>
            <div className="operation-button" id="/" onClick={OperationClick}>/</div>   
        </div>
    )
}


export default OperationButton
