const ReturnButton = (props) => {

    const {setnum, num, newarr, setnewarr} = props


    const clearClick = () => {
        setnewarr([])
        setnum("")
    }
    const backClick = () => {
        setnum(num.slice(0, -1))
    }
    const calculate = (e) => {
        const buttonId = e.target.id
        if(newarr.length === 0){
            setnewarr(newarr => [...newarr, num])
            setnewarr(newarr => [...newarr, (buttonId)])
            setnum('')
        }else {
            switch (newarr[1]) {
                case '+':
                    if (num === '') {
                        setnum(0)
                    }
                    console.log(newarr[0]);
                    console.log(num); 
                    setnewarr(newarr => [Number(parseFloat(Number(newarr[0]) + Number(num)).toPrecision(12)),buttonId])
                    setnum('')
                    break;
                case '-':
                    if (num === '') {
                        setnum(0)
                    }
                    console.log(newarr[0]);
                    console.log(num); 
                    setnewarr(newarr => [Number(parseFloat(Number(newarr[0]) - Number(num)).toPrecision(12)),buttonId])
                    setnum('')
                    break;
                case '*':
                    if (num === '') {
                        setnum(1)
                    }
                    console.log(newarr[0]);
                    console.log(num); 
                    setnewarr(newarr => [Number(parseFloat(Number(newarr[0]) * Number(num)).toPrecision(12)),buttonId])
                    setnum('')
                    break;
                case '/':
                    if (num === '') {
                        setnum(1)
                    }
                    console.log(newarr[0]);
                    console.log(num); 
                    setnewarr(newarr => [Number(parseFloat(Number(newarr[0]) / Number(num)).toPrecision(12)),buttonId])
                    setnum('')
                    break;          
                default:
                    break;
            }
        }
    }
    return (
        <>
            <div className="ac-button" onClick={clearClick}>AC</div>
            <div className="ac-button" onClick={backClick}>â€£</div>
            <div className="ac-button-equal" onClick={calculate}>=</div>
        </>
    )
}


export default ReturnButton
