const OperationButton = (props) => {

    const {setnum, num, newarr, setnewarr} = props


    const handleClick = (e, i) => {
        const numLast = num.length-1
        // console.log(num[0])
        if(num[0] !== undefined ) {
            switch(num[numLast]){
                case '+':
                    setnewarr(num.substr(0,numLast).concat(e.target.id))
                    setnum('')
                    break;

                case '-':
                    setnewarr(num.substr(0,numLast).concat(e.target.id))
                    setnum('')
                    break;

                case '*':
                    setnewarr(num.substr(0,numLast).concat(e.target.id))
                    setnum('')
                    break;

                case '/':
                    setnewarr(num.substr(0,numLast).concat(e.target.id))
                    setnum('')
                    break;

                default:
                    setnewarr(num.concat(e.target.id))
                    setnum('')
                    break;
            }
        }
    }

    return(

        <div className="operation-button-body">
            <div className="operation-button" id="+" onClick={handleClick('+')}>+</div>
            <div className="operation-button" id="-" onClick={handleClick('-')}>-</div>
            <div className="operation-button" id="*" onClick={handleClick('*')}>*</div>
            <div className="operation-button" id="/" onClick={handleClick('/')}>/</div>
        </div>
    )
}


export default OperationButton
