const OperationButton = (props) => {

    const {setnum, num, newarr, setnewarr} = props


    const OperationClick = (e) => {
        const buttonId = e.target.id
        if(newarr.length === 0){
            setnewarr(newarr => [...newarr, num])
            setnewarr(newarr => [...newarr, (buttonId)])
            setnum('')
        }else {
            switch (newarr[1]) {
                case '+':
                    if (num === "") {
                        setnum(0)
                    }
                    console.log(newarr[0]);
                    console.log(num);
                    setnewarr(newarr => [newarr[0] = Number(newarr[0])+num ,newarr[1] = buttonId])
                    console.log(newarr);
                    break;

                default:
                    break;
            }
        }
    }
    console.log(newarr);

    return(

        <div className="operation-button-body">
            <div className="operation-button" id="+" onClick={OperationClick}>+</div>
            <div className="operation-button" id="-" onClick={OperationClick}>-</div>
            <div className="operation-button" id="*" onClick={OperationClick}>*</div>
            <div className="operation-button" id="/" onClick={OperationClick}>/</div>   
        </div>
    )
}


export default OperationButton
