//hooks
import  {useState} from 'react';
//conpoment
import NumberButton from './compoment/NumberButton';
import OperationButton from './compoment/OperationButton';
import ReturnButton from './compoment/ReturnButton';
import Screen from './compoment/Screen';
//css
import './computer.scss'


function App(props) {


    const [number, setnumber] = useState('')
    const newnum = 0
    const addnumber = (number) => {
        newnum = Number(newnum.toString()+number)
        console.log(newnum)
    }
    return (
        <div>
            <div className="wrap">
                <div className="computer">
                    <div className="screen">
                        <Screen number={number}/>
                    </div>
                    <div className="center-button">
                        <NumberButton setnumber={setnumber} number={number}  addnumber={addnumber}/>
                        <OperationButton />
                    </div>
                    <div className="ac-body">
                        <ReturnButton />
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
