//hooks
import { useState } from 'react';
//components
import Number from './compoment/Number';
import OperationButton from './compoment/OperationButton';
import ReturnButton from './compoment/ReturnButton';
import Screen from './compoment/Screen';
//css
import './computer.scss';

function App() {
    const [num, setnum] = useState('');
    const [answer, setanswer] = useState(null);
    const [newarr, setnewarr] = useState([]);

    const handleNumberClick = (value) => {
        setnum((num) => num + value);
    };

    const handleOperationClick = (value) => {
        setnewarr((newarr) => [...newarr, num, value]);
        setnum('');
    };

    const handleReturnClick = () => {
        setnum('');
        setnewarr([]);
        setanswer(null);
    };

    return (
        <div>
            <div className="wrap">
                <div className="computer">
                    <Screen num={num} newarr={newarr} answer={answer} />
                    <div className="center-button">
                        <Number handleNumberClick={handleNumberClick} />
                        <OperationButton handleOperationClick={handleOperationClick} />
                    </div>
                    <div className="ac-body">
                        <ReturnButton handleReturnClick={handleReturnClick} />
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
